#%%
s = ""
s = s.strip("0")
r = []
last = "0"
for i in s:
    if i == last:
        r[-1] += 1
    else:
        r.append(1)
        last = i

res = '<div class="spans">'
res += f'<span style="width: {r[0] / len(s) * 100}%;"></span>'
for i in range(2, len(r), 2):
    res += (
        f'<span style="'
        f'width:{r[i] / len(s) * 100}%;'
        f'margin-left:{r[i-1] / len(s) * 100}%;'
        '"></span>'
    )
res += "</div>"

print(res)
